int i &#x3D; 1234124;
&#x2F;&#x2F;binary expression is 100101101010011001100
&#x2F;&#x2F;also i-1 is          100101101010011001011
&#x2F;&#x2F;the the &amp; operation  100101101010011001000
&#x2F;&#x2F;see! the last 1 is gone
int cnt &#x3D; 0;
while(i)&#123;
    i &amp;&#x3D; (i-1);
    cnt++;
&#125;
cout &lt;&lt; cnt &lt;&lt; endl;
&#x2F;&#x2F;the outcome is 10

前言
在比赛中经常会有通用的方法，在时间紧迫的比赛中花时间默写已有的模版是很耗费时间的，在套用模版后加上针对性改进往往会有事半功倍的效果，但切忌套用不熟悉的模版。在搞清楚原理后使用才能达成节省时间、专注于解决问题上。以下模版为个人使用，是简化的版本，如并查集没有按轶合并等，在效率上略显底下。


质数打表void solve()&#123;
    int n; cin &gt;&gt; n;
    vector&lt;int&gt; d(n,1);
    for(int i &#x3D; 2; i &lt;&#x3D; n; i++)&#123;
        if(!d[i]) continue;
        for(int j &#x3D; 2; j &lt; i; j++)&#123;
            if(i%j &#x3D;&#x3D; 0)&#123;
                for(int k &#x3D; 1; k * i &lt;&#x3D; n; k++)&#123;
                    d[k*i] &#x3D; false; 
                &#125;
            &#125;
        &#125;
    &#125;
    cout &lt;&lt; &quot;vector&lt;int&gt; primes &#x3D; &#123;&quot;;
    for(int i &#x3D; 1; i &lt;&#x3D; n; i++)&#123;
        if(d[i]) &#123;
            if(i &gt; 0) cout &lt;&lt; &#39;,&#39;;
            cout &lt;&lt; i;
        &#125;
    &#125;
    cout &lt;&lt; &quot;&#125;;&quot;;
&#125;

链式前向星存图const int maxn &#x3D; 1e6;
struct E&#123;
    int to,w,next;
&#125;Edge[maxn];
int tot &#x3D; 0, h[maxn];
inline void addegde(int u,int v,int w)&#123;
    Edge[tot].to &#x3D; v;
    Edge[tot].w &#x3D; w;
    Edge[tot].next &#x3D; h[u];
    h[u] &#x3D; tot++;
&#125;

并查集const int maxn &#x3D; 100005;
int f[maxn];
void init(int n)&#123;
    for(int i &#x3D; 0; i &lt; n; i++)&#123;
        f[i] &#x3D; i;
    &#125;
&#125;
int find(int x)&#123;
    return x &#x3D;&#x3D; f[x] ? x : f[x] &#x3D; find(f[x]);
&#125;
void merge(int a,int b)&#123;
    f[find(a)] &#x3D; find(b);
&#125;

后缀自动机struct Node&#123;
    int len, link;
    unordered_map&lt;int,int&gt; go;
&#125;a[20000];
int sz,last;
void saminit()&#123;
    sz &#x3D; last &#x3D; 0;
    a[0].len &#x3D; 0;
    a[0].link &#x3D; -1;
    ++sz;
&#125;
void add(char c)&#123;
    int cur &#x3D; sz++;
    a[cur].len &#x3D; a[last].len+1;
    int p &#x3D; last;
    while(p !&#x3D; -1 &amp;&amp; !a[p].go.count(c-&#39;a&#39;))&#123;
        a[p].go[c-&#39;a&#39;] &#x3D; cur;
        p &#x3D; a[p].link;  
    &#125;
    if(p &#x3D;&#x3D; -1) a[cur].link &#x3D; 0;
    else&#123;
        int q &#x3D; a[p].go[c-&#39;a&#39;];
        if(a[q].len &#x3D;&#x3D; a[p].len+1) a[cur].link &#x3D; q;
        else&#123;
            int clone &#x3D; sz++;
            a[clone].len &#x3D; a[p].len+1;
            a[clone].go &#x3D; a[q].go, a[clone].link &#x3D; a[q].link;
            while(p !&#x3D; -1 &amp;&amp; a[p].go[c-&#39;a&#39;] &#x3D;&#x3D; q)&#123;
                a[p].go[c-&#39;a&#39;] &#x3D; clone;
                p &#x3D; a[p].link;
            &#125;
            a[cur].link &#x3D; a[q].link &#x3D; clone;
        &#125;
    &#125;
    last &#x3D; cur;
&#125;
